pragma solidity ^0.4.16;

contract FourInARow {
	mapping (uint8=>bytes32[2]) gameMoves;
	mapping (uint8=>int8[2]) gameLastMoveIndex;
	mapping (uint8=>address[2]) gamePlayers;
	uint8 gameCount;

	function playerMoves(uint8 gameId, uint8 playerNr) public view returns (bytes32 moves) {
		return moves[gameId][playerNr];
	}

	event Move(uint8 indexed _gameId, uint8 _playerNr, uint8 _colNr);

	function FourInARow() public {
		gameCount = 0;
	}

	function createGame() public returns (uint8 gameid) {
		// todo: take msg.value as stake (including require(msg.value > 0))

		gameCount++;
		gameid = gameCount - 1;

		address[2] memory players;
		players[0] = msg.sender; // p1
		players[1] = address(0); // p2 (joins later)
		gamePlayers[gameid] = players;

		bytes32[2] memory moves;
		gameMoves[gameid] = moves;

		int8[2] memory lastMoveIndexes;
		lastMoveIndexes[0] = -1;
		lastMoveIndexes[1] = -1;
		gameLastMoveIndex[gameid] = lastMoveIndexes;

		return gameid;
	}

	function joinGame(uint8 gameid) {
		require(gameid >= 0);
		require(gameid < (gameCount - 1));
		require(gamePlayers[gameid][1] != address(0));

		gamePlayers[gameid][1] = msg.sender;
		// todo: process msg.value as matching stake
	}

	function getPlayerIndex(uint8 gameid) public view returns (uint8 playerIndex) {
		require(gameid >= 0);
		require(gameid < (gameCount - 1));

		if (gamePlayers[gameid][0] == msg.sender) {
			return 0;
		} else if (gamePlayers[gameid][1] == msg.sender) {
			return 1;
		}
		revert(); // throw
	}

	function makeMove(uint8 gameid, uint8 col) public returns(bool isWinner) {
		require(gameid >= 0);
		require(gameid < (gameCount - 1));
		require(col >= 0 && col <= 7);

